import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { ApiService } from '../../../servicios/api.service';

@Component({
  selector: 'app-soft-skills-create',
  templateUrl: './soft-skills-create.component.html',
  styleUrls: ['./soft-skills-create.component.css']
})
export class SoftSkillsCreateComponent implements OnInit {

  form:FormGroup;
  id: string = '';
  softSkill: any;

  constructor(
    private formBuilder:FormBuilder,
    private apiService: ApiService,
    private router: Router,
    private route: ActivatedRoute
  ){
    this.form = this.formBuilder.group({
      //ACA SE PUEDE PONER SI ES MAIL O OTROS VALIDATORS
      nombreHabilidad:['', [Validators.required, Validators.minLength(5)]],
      porcentaje:['', [Validators.required, Validators.minLength(5)]],
    });
  }

  ngOnInit(): void {
  }

  get NombreHabilidad(){
    return this.form.get('nombreHabilidad');
  }
  get Porcentaje(){
    return this.form.get('porcentaje');
  }

  onEnviar(event:Event){
    event.preventDefault();
    this.apiService.updateSoftSkill(this.form.value, this.route.snapshot.paramMap.get('id'))
      .subscribe(data=>{
        this.router.navigate(['portfolio'])
      })
  }
}
