import { NgModule } from '@angular/core';
import { RouterModule, Routes } from '@angular/router'
import { PortfolioComponent } from './components/portfolio/portfolio.component';
import { LoginComponent } from './components/login/login.component';
import { AuthGuard } from './helpers/auth.guard';
import { EducationFormComponent } from './components/education/education-form/education-form.component';
import { EducationUpdateComponent } from './components/education/education-update/education-update.component';

const routes: Routes = [
  { path: 'portfolio', component:PortfolioComponent, canActivate: [AuthGuard]},
  // {
  //   path: 'educaciones', 
  //   component:EducationComponent,
  //   canActivate: [AuthGuard]
  // },
  { path: 'educacionCrear', component:EducationFormComponent, canActivate: [AuthGuard]},
  { path: 'educacionEdit/:id', component:EducationUpdateComponent, canActivate: [AuthGuard]},
  { path: 'login', component:LoginComponent },
  { path: '', redirectTo: 'login', pathMatch: 'full' }
]

@NgModule({
  imports:[RouterModule.forRoot(routes)],
  exports: [RouterModule]
})

export class AppRoutingModule { }
